sp_widget.client_script:
function clientFun($rootScope,$scope) {
  /* widget controller */
  var c = this;
  c.selectItem = function(idx) {
    var id = c.data.notes[idx].sys_id;
    console.log('Note ID: ' + id);
    $rootScope.noteID = id;
    $rootScope.$emit('selectNote', id);
  }
}

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */

	data.notes = [];
	var noteGR = new GlideRecord('x_58872_needit_needit');
	noteGR.addQuery('active', true);
	noteGR.addQuery('short_description', '!=', '');
	// noteGR.addQuery('assigned_to', gs.getUser().getID());
	noteGR.orderByDesc('sys_created_on');
	noteGR.query();
	while(noteGR.next()) {
		var noteObj = {};
		$sp.getRecordDisplayValues(noteObj, noteGR, 'number,sys_id,short_description');
		noteObj.note = (noteGR.getValue('short_description') || '').slice(0,20);
		data.notes.push(noteObj);
	}
})();
